const defaultConfig = {
  iceServers: [{
    urls: 'stun:stun.webrtc.ecl.ntt.com:3478',
    url:  'stun:stun.webrtc.ecl.ntt.com:3478',
  }],
  iceTransportPolicy: 'all',
};

const peer = new Peer({
    key:   "<YOUR-API-KEY>"
    debug: 3,
});

// 自身のlocalStreamを設定して、相手に発信する場合
const call = peer.call('peerID', localStream);

// 映像コーデックとしてH264を利用する場合
const call = peer.call('peerID', localStream, {
  videoCodec: 'H264',
});

// 音声のみ接続先から受信する設定で、相手に発信する場合
const call = peer.call('peerID', localStream, {
  audioReceiveEnabled: true,
});

// 単にDataChannelを接続する場合(デフォルトで信頼性有り)
peer.connect('peerId');

// metadata付きでconnectする場合
peer.connect('peerId', {
  metadata: {
    hoge: "foobar",
  }
});

// 信頼性無モードでDataChannelを接続する場合
peer.connect('peerId', {
  dcInit: {
    // 最大2回、再送する
    maxRetransmits: 2,
  },
});

// Mesh接続を利用する場合
const room = peer.joinRoom("roomName", {
  mode: 'mesh', 
  stream: localStream,
});

// SFU接続を利用する場合
const room = peer.joinRoom("roomName", {
  mode: 'sfu', 
  stream: localStream,
});

peer.listAllPeers(peers => {
  console.log(peers)
  // => ["yNtQkNyjAojJNGrt", "EzAmgFhCKBQMzKw9"]
});

peer.on('open', id => {
  console.log(id);
})

peer.on('call', call => {
  // 着信側のメディアストリームを設定して応答
  call.answer(mediaStream);
});

peer.on('connection', connection => {
  console.log(connection);
});

// 仮にRoom名を指定せずにjoinRoomを呼んだ場合
peer.on('error', error => {
  console.log(`${error.type}: ${error.message}`);
  // => room-error: Room name must be defined.
});

// 相手から発信を受けて
peer.on('call', call => {
  call.answer(mediaStream);
});

// metadataのサンプルをここに
peer.on('connection', connection => {
  console.log(connection.metadata);
  // => connect時に付与した、metadataを参照する
});

